mapper.select:
SELECT count(1) FROM tb_local_auth
and user_name like '%${LocalAuthCondition.userName}%'
and user_id = #{LocalAuthCondition.userId}
and local_auth_id = #{LocalAuthCondition.localAuthId}

SELECT
local_auth_id,
user_name,
password,
create_time,
last_edit_time,
user_id
FROM tb_local_auth



		and user_name like '%${LocalAuthCondition.userName}%'


		and user_id = #{LocalAuthCondition.userId}


		and local_auth_id = #{LocalAuthCondition.localAuthId}




	ORDER BY ${sort} ${order}

LIMIT #{rowIndex},#{pageSize};

mapper.update:
update tb_local_auth

	password=#{newPassword},
	last_edit_time=#{lastEditTime}

where user_id=#{userId}
AND user_name=#{userName}
AND
password=#{password}
